mapper.insert:
/* ncis.sql.cmn.cmAlrm.insertCmInstitution */
	INSERT INTO CM_ALRM (
		ALRM_SBJCT
		,ALRM_CN
		,CHARGER_ID
		,TRGT_URL
		,STAT_CD
		,REG_DTTM
) VALUES (
	#{alrmSbjct }
	,#{alrmCn }
	,#{chargerId }
	,#{trgtUrl }
	,#{statCd }
	,#{regDttm }
)

mapper.update:
/* ncis.sql.cmn.cmAlrm.updateCmAlrm */
	UPDATE CM_ALRM SET
		CONFRM_YN = #{confrmYn },
		CONFRM_DTTM = now()
	WHERE ALRM_SEQ = #{alrmSeq}
