mapper.insert:
/* ncis.sql.cmn.cmBtchWrk.insertCmBtchWrk */
SELECT nextval('BTCH_WRK_SEQ')
INSERT INTO CM_BTCH_WRK ( BTCH_WRK_SEQ /* 배치작업SEQ */ , BTCH_WRK_ID /* 배치 JOB ID */ , BTCH_WRK_NM /* 배치작업명 */ , CYCLE /* 주기 */ , CYCLE_DIRECT_WRITE /* 주기직접입력 */ , EXE_TIME_MONTH /* 실행시점월 */ , EXE_TIME_DAY /* 실행시점일 */ , EXE_TIME_HOUR /* 실행시점시 */ , EXE_TIME_MINUTE /* 실행시점분 */ , TIME_TYPE /* 주기/시점 구분 */ , STAT /* 상태 */ , BTCH_WRK_FILE_NM /* 배치 파일 명 */ , EXE_RSRV_DTTM /* 실행예약일시 */ , STOP_RSRV_DTTM /* 정지예약일시 */ , EXE_OPTN /* 실행옵션 */ , DC /* 설명 */ , REG_USER_ID /* 등록사용자ID */ , REG_DT /* 등록일자 */ ) VALUES ( #{btchWrkSeq} /* 배치작업SEQ */ , #{btchWrkId} , #{btchWrkNm} /* 배치작업명 */ , #{cycle} /* 주기 */ , #{cycleDirectWrite} /* 주기직접입력*/ , #{exeTimeMonth} /* 실행시점월 */ , #{exeTimeDay} /* 실행시점일 */ , #{exeTimeHour} /* 실행시점시 */ , #{exeTimeMinute} /* 실행시점분 */ , #{timeType} /* 주기/시점 구분 */ , #{stat} /* 상태 */ , #{btchWrkFileNm} /* 배치 파일 명 */ , #{exeRsrvDttm} /* 실행예약일시 */ , #{stopRsrvDttm} /* 정지예약일시 */ , #{exeOptn} /* 실행옵션 */ , #{dc} /* 설명 */ , #{regUserId} /* 등록사용자ID */ , now() /* 등록일자 */ )

mapper.delete:
	/* ncis.sql.cmn.cmBtchWrk.deleteCmBtchWrk */
	DELETE FROM CM_BTCH_WRK
         WHERE BTCH_WRK_SEQ = #{value }  /* 배치작업SEQ */
