mapper.insert:
/* ncis.sql.cmn.siRsrcPoolUser.insertCmRsrcPoolUser */
INSERT INTO CM_RSRC_POOL_USER (
             RSRC_POOL_ID  /* 자원풀ID */
           , USER_ID  /* 사용자ID */
) VALUES (
           #{rsrcPoolId}  /* 자원풀ID */
         , #{userId}  /* 사용자ID */
)

mapper.delete:
	/* ncis.sql.cmn.siRsrcPoolUser.deleteCmRsrcPoolUser */
	DELETE FROM CM_RSRC_POOL_USER
         WHERE RSRC_POOL_ID = #{rsrcPoolId}  /* 자원풀ID */
