mapper.insert:
/* ncis.sql.cmn.seqNumInfo.insertSeqNumInfo */
INSERT INTO SEQ_NUM_INFO (
             SEQ_NUM_CL  /* 채번구분 */
           , SEQ_NUM_DC  /* 채번설명 */
) VALUES (
           #{seqNumCl}  /* 채번구분 */
         , #{seqNumDc}  /* 채번설명 */
)

mapper.delete:
	/* ncis.sql.cmn.seqNumInfo.deleteSeqNumInfo */
	DELETE FROM SEQ_NUM_INFO
         WHERE SEQ_NUM_CL = #{seqNumCl}  /* 채번구분 */
