mapper.delete:
	/* ncis.sql.cmn.rnIp.deleteRnIp */
	DELETE FROM RN_IP
         WHERE IP_ADDR = #{ipAddr}::inet AND BND_SEQ = #{bndSeq} /* IP주소 */

mapper.update:
/* ncis.sql.cmn.rnIp.updateIpSyncronize */ UPDATE RN_IP A SET (IP_STAT_CD, NETWK_INTFC_SEQ, CHNG_DT, ASGN_DT, WITHDRAW_DT) = ( SELECT (CASE WHEN E.NETWK_INTFC_NM IS NULL THEN '02' WHEN E.NETWK_INTFC_NM IS NOT NULL THEN '01' END ) AS IP_STAT_CD, E.NETWK_INTFC_SEQ, CURRENT_DATE, (CASE WHEN E.NETWK_INTFC_NM IS NOT NULL AND IP_STAT_CD = '01' THEN ASGN_DT WHEN E.NETWK_INTFC_NM IS NOT NULL THEN CURRENT_DATE END ) AS ASGN_DT, (CASE WHEN E.NETWK_INTFC_NM IS NULL AND IP_STAT_CD = '02' THEN WITHDRAW_DT WHEN E.NETWK_INTFC_NM IS NULL THEN CURRENT_DATE END ) AS WITHDRAW_DT FROM RN_IP C INNER JOIN RN_IP_BND D ON C.BND_SEQ = D.BND_SEQ LEFT JOIN ( SELECT B.VM_SEQ, E.VM_ID, B.NETWK_INTFC_SEQ, B.UUID, B.IP_ADDR, B.NIC_PRPOS_CD, NULL NETWK_INTFC_ID, B.NETWK_INTFC_NM, E.INSTITUTION_ID FROM RC_NETWK_INTFC B , RC_VM E WHERE B.DEL_YN = 'N' AND B.VM_SEQ = E.VM_SEQ AND E.DEL_YN = 'N' AND B.UUID IS NOT NULL AND B.NIC_PRPOS_CD IS NOT NULL UNION ALL SELECT NULL VM_SEQ, C.VM_ID, NULL NETWK_INTFC_SEQ, NULL UUID, A.IP_ADDR, A.NIC_PRPOS_CD, NETWK_INTFC_ID, NETWK_INTFC_NM, C.USE_GV_DPRT_ID FROM RR_NETWK_INTFC_REQ_LIST A , RR_RSRC_REQ B , RR_RSRC_REQ_DTL_VM C WHERE A.RSRC_REQ_NO = B.RSRC_REQ_NO AND A.RSRC_REQ_NO = C.RSRC_REQ_NO AND A.RSRC_REQ_SEQ = C.RSRC_REQ_SEQ AND B.RSRC_REQ_PRCSS_STAT_CD = '02' AND B.DEL_YN = 'N' ORDER BY VM_SEQ NULLS LAST, VM_ID, NETWK_INTFC_NM ) E ON C.IP_ADDR = E.IP_ADDR::inet AND EXISTS ( SELECT 1 FROM RN_IP_BND_PRPOS X WHERE C.BND_SEQ = X.BND_SEQ AND E.NIC_PRPOS_CD = TO_CHAR(FLOOR(TO_NUMBER(X.PRPOS_CL_CD,'FM99999')/4)+1,'FM09') ) AND (EXISTS ( SELECT 1 FROM RN_IP_BND_INSTITUTION X WHERE X.INSTITUTION_ID = E.INSTITUTION_ID AND D.BND_SEQ = X.BND_SEQ ) OR C.NETWK_INTFC_SEQ = E.NETWK_INTFC_SEQ ) WHERE A.BND_SEQ = C.BND_SEQ AND A.IP_ADDR = C.IP_ADDR LIMIT 1 ), UPDT_USER_ID = #{updtUserId} WHERE A.IP_STAT_CD != '03' /* Block인 경우 동기화 제외*/
AND A.BND_SEQ IN (
#{bndSeq}
)
