mapper.insert:
/* ncis.sql.cmn.rnIpBnd.insertRnIpBnd */
SELECT nextval('RN_IP_BND_SQ01')
INSERT INTO RN_IP_BND ( BND_SEQ /* 대역SEQ */ , IP_BND_NM /* IP대역명 */ , USE_YN /* 사용여부 */ , BND_STRT_IP /* 대역시작IP주소 */ , BND_END_IP /* 대역종료IP주소 */ , SUBNET_MASK /* 서브넷마스크 */ , GTWY_IP_ADDR /* 게이트웨이IP주소 */ , VLAN /* VLAN */ , ASGN_IP_QTY /* 할당IP수 */ , UNASGN_IP_QTY /* 미할당IP수 */ , BLK_IP_QTY /* 블럭IP수 */ , REG_USER_ID /* 등록자ID */ , REG_DTTM /* 등록일시 */ , UPDT_USER_ID /* 수정자ID */ , UPDT_DTTM /* 수정일시 */ , NET_CL_CD /* 망ID */ , REGION_ID /* 리전ID */ ) VALUES ( #{bndSeq} /* 대역SEQ */ , #{ipBndNm} /* IP대역명 */ , #{useYn} /* 사용여부 */ , #{bndStrtIp} /* 대역시작IP주소 */ , #{bndEndIp} /* 대역종료IP주소 */ , #{subnetMask} /* 서브넷마스크 */ , #{gtwyIpAddr} /* 게이트웨이IP주소 */ , #{vlan} /* VLAN */ , #{asgnIpQty} /* 할당IP수 */ , #{unasgnIpQty} /* 미할당IP수 */ , #{blkIpQty} /* 블럭IP수 */ , #{regUserId} /* 등록자ID */ , now() /* 등록일시 */ , #{updtUserId} /* 수정자ID */ , now() /* 수정일시 */ , #{netClCd} /* 망ID */ , #{regionId} /* 리전ID */ )

mapper.delete:
/* ncis.sql.cmn.rnIpBnd.deleteRnIpBnd */
DELETE FROM RN_IP_BND
	WHERE BND_SEQ = #{bndSeq}
