mapper.insert:
/* ncis.sql.cmn.rnIpBndPrpos.insertRnIpBndPrpos */
INSERT INTO RN_IP_BND_PRPOS (
             BND_SEQ  /* 대역SEQ */
           , PRPOS_CL_CD  /* 용도구분코드 */
) VALUES (
           #{bndSeq}  /* 대역SEQ */
         , #{prposClCd}  /* 용도구분코드 */
)

mapper.delete:
	/* ncis.sql.cmn.rnIpBndPrpos.deleteRnIpBndPrposByBndSeq */
	DELETE FROM RN_IP_BND_PRPOS
         WHERE BND_SEQ = #{bndSeq}  /* 대역SEQ */
