mapper.insert:
/* ncis.sql.cpt.rnSRout.insertRnSRout */
SELECT nextval('RN_S_ROUT_SQ01')
INSERT INTO RN_S_ROUT ( S_ROUT_SEQ /* 스태틱라우터SEQ */ , IP_BND_ADDR /* IP대역주소 */ , SUBNET_MASK /* 서브넷마스크 */ , GTWY_NM /* 게이트웨이명 */ , BND_SEQ /* 대역SEQ */ ) VALUES ( #{sRoutSeq} /* 스태틱라우터SEQ */ , #{ipBndAddr} /* IP대역주소 */ , #{subnetMask} /* 서브넷마스크 */ , #{gtwyNm} /* 게이트웨이명 */ , #{bndSeq} /* 대역SEQ */ )

mapper.delete:
/* ncis.sql.cpt.rnSRout.deleteRnSRout */
DELETE FROM RN_S_ROUT
WHERE
	BND_SEQ = #{bndSeq}
