/* ncis.sql.cmn.rnSlbIp.updateRnSlbIp */
UPDATE RN_SLB_IP
SET IP_ADDR = #{ipAddr}  /* IP주소 */
	, PORT = #{port}  /* 포트 */
	, VIP_ADDR = #{vipAddr}  /* VIP주소 */
	, WVL = #{wvl}  /* 가중치(로드밸런싱) */
	, DC = #{dc}  /* 설명 */
WHERE IP_ADDR = #{ipAddr}  /* IP주소 */
	AND VM_SEQ = #{vmSeq}
	AND VIP_ADDR = #{vipAddr}
