mapper.delete:
	/* ncis.sql.cmn.rrRsrcReq.deleteRrRsrcReq */
	DELETE FROM RR_RSRC_REQ
         WHERE RSRC_REQ_NO = #{rsrcReqNo}  /* 자원요청번호 */

mapper.update:
/* ncis.sql.cmn.rrRsrcReq.updateRsrcReqPrcssStat */ UPDATE RR_RSRC_REQ SET RSRC_REQ_PRCSS_STAT_CD = #{rsrcReqPrcssStatCd} /* 자원요청진행상태코드 */
,EXE_USER_ID ='' /* 실행자ID */
WHERE RSRC_REQ_NO = #{rsrcReqNo} /* 자원요청번호 */
