UPDATE RX_BLD_CONF
SET
	SCRTKY_ID = #{scrtkyId}
	,GIT_BRNCH_NM = #{gitBrnchNm}
	,GIT_BEST_PATH = #{gitBestPath}
	,REPO_ADDR = #{repoAddr}
	,BAS_IMG_ID = #{basImgId}
	,UPDT_DTTM = now()
	,UPDT_USER_ID = #{updtUserId}
	,LAST_BLD_VER = #{lastBldVer}
	,LAST_BLD_DTTM = now()
WHERE SERVC_SEQ = #{servcSeq} AND BLD_ID = #{bldId}
