            , PV_ID
            , STAT_CD
            )
    VALUES
    		(
    		  #{servcAreaSeq}
    		, #{pvcId}
    		, #{pvcNm}
    		, #{pvcUid}
    		, #{reqStrgCapa}
    		, #{accssModeClCd}
    		, now()
    		, #{pvId}
    		, #{statCd}
    		);

	/*ncis.sql.cmn.rxDistrbConf.insertDistrbAsgn*/
