            )
   		 VALUES
   		  (
			 #{servcAreaSeq}
			,#{servcSeq}
			,#{distrbConfId}
			,#{pvcId}
			,#{volumeNm}
			,#{mountPath}
			,now()
		  );

	/*ncis.sql.cmn.rxDistrbConf.insertReplicasAdd*/
