mapper.delete:
		/* ncis.sql.cmn.RxPv.deleteRxPv */
		UPDATE RX_PV
        SET SERVC_AREA_SEQ = NULL
		, UPDT_DTTM = now()
        WHERE RSRC_POOL_ID = #{rsrcPoolId}

		AND PV_ID = #{pvId}


		AND SERVC_AREA_SEQ = #{servcAreaSeq}

mapper.update:
		/* ncis.sql.cmn.RxPv.updateRxPv */
		UPDATE RX_PV
		SET SERVC_AREA_SEQ = #{servcAreaSeq}
		, UPDT_DTTM = now()
        WHERE PV_ID = #{pvId}
        AND RSRC_POOL_ID = #{rsrcPoolId}
