mapper.update:
		/* ncis.sql.cmn.rxServcPod.updateRxServcPod */
		UPDATE RX_SERVC_POD
		SET STRT_DTTM = #{strtDttm}::timestamp
		,UPDT_DTTM = now()
		,DEL_YN = 'N'

		,STAT_CD = #{statCd}

        WHERE SERVC_SEQ = #{servcSeq}

			AND POD_ID = #{podId}

mapper.delete:
		/* ncis.sql.cmn.rxServcPod.deleteRxServcPod */
		UPDATE RX_SERVC_POD
		SET DEL_YN = 'Y'
        WHERE SERVC_SEQ = #{servcSeq}  /* 서비스SEQ */

			AND POD_ID = #{podId}
