mapper.delete:
	/* ncis.sql.cmn.stEvnt.deleteStEvnt */
	DELETE FROM ST_EVNT
         WHERE EVNT_SEQ = #{evntSeq}  /* 이벤트ID */

mapper.update:
/* ncis.sql.cmn.stEvnt.updateEvntConfrm */
	UPDATE ST_EVNT
	SET 	CONFRM_USER_ID=#{confrmUserId}
		, CONFRM_DTTM= NOW()
	WHERE EVNT_SEQ = #{evntSeq}  /* 이벤트ID */
