/*ncis.sql.dsb.stts.specusert.memUseRt.selectMemUseRtList*/ SELECT CASE WHEN CAST(A.CAPA AS CHARACTER VARYING)='888' THEN '기타' WHEN CAST(A.CAPA AS CHARACTER VARYING)='999' THEN '계' ELSE CAST(A.CAPA AS CHARACTER VARYING) END CAPA, COALESCE(B.USE_RT10,0) USE_RT10, COALESCE(B.USE_RT20,0) USE_RT20, COALESCE(B.USE_RT30,0) USE_RT30, COALESCE(B.USE_RT40,0) USE_RT40, COALESCE(B.USE_RT50,0) USE_RT50, COALESCE(B.USE_RT60,0) USE_RT60, COALESCE(B.USE_RT70,0) USE_RT70, COALESCE(B.USE_RT80,0) USE_RT80, COALESCE(B.USE_RT90,0) USE_RT90, COALESCE(B.USE_RT100,0) USE_RT100, COALESCE(B.USE_RT10,0)+COALESCE(B.USE_RT20,0)+COALESCE(B.USE_RT30,0)+COALESCE(B.USE_RT40,0)+COALESCE(B.USE_RT50,0)+ COALESCE(B.USE_RT60,0)+COALESCE(B.USE_RT70,0)+COALESCE(B.USE_RT80,0)+COALESCE(B.USE_RT90,0)+COALESCE(B.USE_RT100,0) TOTAL FROM (SELECT 8 CAPA UNION ALL SELECT 16 UNION ALL SELECT 32 UNION ALL SELECT 64 UNION ALL SELECT 128 UNION ALL SELECT 888 UNION ALL SELECT 999) A LEFT JOIN (SELECT COALESCE(CAPA,999) CAPA, SUM(USE_RT10) USE_RT10, SUM(USE_RT20) USE_RT20, SUM(USE_RT30) USE_RT30, SUM(USE_RT40) USE_RT40, SUM(USE_RT50) USE_RT50, SUM(USE_RT60) USE_RT60, SUM(USE_RT70) USE_RT70, SUM(USE_RT80) USE_RT80, SUM(USE_RT90) USE_RT90, SUM(USE_RT100) USE_RT100 FROM ( SELECT CASE WHEN ROUND(LAST_MEM_SUM_CAPA/1024/1024/1024 ,0) <=8 THEN 8 WHEN ROUND(LAST_MEM_SUM_CAPA/1024/1024/1024 ,0) <=16 THEN 16 WHEN ROUND(LAST_MEM_SUM_CAPA/1024/1024/1024 ,0) <=32 THEN 32 WHEN ROUND(LAST_MEM_SUM_CAPA/1024/1024/1024 ,0) <=64 THEN 64 WHEN ROUND(LAST_MEM_SUM_CAPA/1024/1024/1024 ,0) <=128 THEN 128 ELSE 888 END CAPA, CASE WHEN AVG_MEM_USE_RT <=10 THEN 1 END USE_RT10, CASE WHEN AVG_MEM_USE_RT >10 AND AVG_MEM_USE_RT <=20 THEN 1 END USE_RT20, CASE WHEN AVG_MEM_USE_RT >20 AND AVG_MEM_USE_RT <=30 THEN 1 END USE_RT30, CASE WHEN AVG_MEM_USE_RT >30 AND AVG_MEM_USE_RT <=40 THEN 1 END USE_RT40, CASE WHEN AVG_MEM_USE_RT >40 AND AVG_MEM_USE_RT <=50 THEN 1 END USE_RT50, CASE WHEN AVG_MEM_USE_RT >50 AND AVG_MEM_USE_RT <=60 THEN 1 END USE_RT60, CASE WHEN AVG_MEM_USE_RT >60 AND AVG_MEM_USE_RT <=70 THEN 1 END USE_RT70, CASE WHEN AVG_MEM_USE_RT >70 AND AVG_MEM_USE_RT <=80 THEN 1 END USE_RT80, CASE WHEN AVG_MEM_USE_RT >80 AND AVG_MEM_USE_RT <=90 THEN 1 END USE_RT90, CASE WHEN AVG_MEM_USE_RT >90 THEN 1 END USE_RT100, AVG_MEM_USE_RT FROM (SELECT VM_SEQ, MAX(LAST_MEM_SUM_CAPA) LAST_MEM_SUM_CAPA, CASE WHEN SUM(SUM_MEM_SUM_CAPA)=0 THEN 0 ELSE SUM(SUM_MEM_USE_CAPA)/ SUM(SUM_MEM_SUM_CAPA)*100 END AVG_MEM_USE_RT FROM ( SELECT VM_SEQ, FIRST_VALUE(LAST_MEM_SUM_CAPA) OVER (PARTITION BY VM_SEQ ORDER BY COLCT_YM DESC) LAST_MEM_SUM_CAPA, SUM_MEM_USE_CAPA, SUM_MEM_SUM_CAPA FROM ST_VM_COLCT_SUM_1MM WHERE 1=1
AND REGION_ID = #{regionId}
AND COLCT_YM BETWEEN #{year}||'01' AND #{year}||'03'
AND COLCT_YM BETWEEN #{year}||'04' AND #{year}||'06'
AND COLCT_YM BETWEEN #{year}||'07' AND #{year}||'09'
AND COLCT_YM BETWEEN #{year}||'10' AND #{year}||'12'
AND COLCT_YM LIKE #{year}||'%'
AND COLCT_YM = #{year}||#{month}
) A GROUP BY VM_SEQ ) A ) A GROUP BY GROUPING SETS((CAPA),())) B ON A.CAPA = B.CAPA ORDER BY A.CAPA

